import React from "react";
import personImg from "../../assets/images/person.png";

const Users = (props) => {
  console.log(props);

  let showUsers = () => {
    return props.users.map((u, index) => {
      return (
        <div key={index}>
          {u.name} id={u.id}
          <span>
            <div className="user-photo">
              <img src={u.photos.small ? u.photos.small : personImg} alt="ss" />
            </div>
            <div className="">
              {!u.followed ? (
                <button onClick={() => props.follow(u.id)}>Follow</button>
              ) : (
                <button onClick={() => props.unfollow(u.id)}>
                  unFollow
                </button>
              )}
              {/* <button onClick={cruser}>createUser</button> */}
            </div>
          </span>
          <span>
            <span>
              <div className="">{u.fullname} </div>
              <div className="">{u.status} </div>
            </span>
            <span>
              <div className="">{"u.location.country"} </div>
              <div className="">{"u.location.city"} </div>
            </span>
          </span>
        </div>
      );
    });
  };

  return (
    <div className="">



        {<div className="">{showUsers()}</div>}
        <hr />
    </div>

  )
}

export default Users
