import React from "react";
import './Profileinfo.css'
import '../../../assets/css/socialmedia.css'
import MyPostsContainer from '../MyPosts/MyPostsContainer'
import Preloader from '../../commonBlocks/Preloader/PreloaderWaive'
import personImg from "../../../assets/images/anonyme.png";

const Profileinfo = (props) => {
  /* window.bb = props.profile.photos */
  //!props.profile && <Preloader /> //Empty data test 

  const smallPhoto = () => (
    props.profile.photos.small ? props.profile.photos.small : personImg
  )

  const userContacts = () => {
    const arrContacts = Object.entries(props.profile.contacts);
    window.c1 = arrContacts
    window.cc = arrContacts.map((a,b)=>(`${a}  ${b}`))
    //return  arrContacts.map((sosialSource,key)=>(<p> sosialSource: {sosialSource}  </p>))
    return  arrContacts.map((arrC,key)=>(
      arrC.map((media,key) => ((<p> media: {media} key: {key} </p>)))
      ))
  }

  const isPropsTxt = () => (
    <div>

      <div className="x">
          <h2>avatar + description</h2> 
          <a href="#" className="fa fa-facebook"></a>
          <div className="user-photo__small">
            <img src={smallPhoto()} alt="small Photo" />
          </div>
          <p>About me: {props.profile.aboutMe ? props.profile.aboutMe : '...'}</p>
          <p>Contacts: {userContacts()}</p>

          <a href="#" className="fa fa-facebook"></a>
          <a href="#" className="fa fa-external-link"></a>  

<a href="#" className="fa fa-twitter"></a>
<a href="#" className="fa fa-google"></a>
<a href="#" className="fa fa-linkedin"></a>
<a href="#" className="fa fa-youtube"></a>
<a href="#" className="fa fa-instagram"></a>
<a href="#" className="fa fa-pinterest"></a>
<a href="#" className="fa fa-snapchat-ghost"></a>
<a href="#" className="fa fa-skype"></a>
<a href="#" className="fa fa-android"></a>
<a href="#" className="fa fa-dribbble"></a>

<a href="#" className="fa fa-tumblr"></a>
<a href="#" className="fa fa-vine"></a>
<a href="#" className="fa fa-foursquare"></a>
<a href="#" className="fa fa-stumbleupon"></a>
<a href="#" className="fa fa-flickr"></a>
<a href="#" className="fa fa-yahoo"></a>
<a href="#" className="fa fa-reddit"></a>
<a href="#" className="fa fa-rss"></a>

          .
      </div>
      {/* <MyPostsContainer /> */}
    </div>
  )

  return (
    !props.profile ? <Preloader /> : isPropsTxt()
  )
}

export default Profileinfo;
