import React from "react";
import Users from './Users'
import { followAC, unfollowAC, setUsersAC } from '../../redux/reducer-users'
//{followAC, unfollowAC, setUsersAC} //reducer-users
import { connect } from 'react-redux'


const mapStateToProps = (state) => {
  //console.log('hello!')
  return {
    posts: state.profileReducer.posts,
    newPostText: state.profileReducer.newPostText
  }
}

const mapDispatchToProps = (dispatch) => {  
  return {
    addPost: () => {
      dispatch(addPostActionCreator())
    },
    updateNewPostText: (textareaOnChange) => {
      dispatch(updateNewPostTextActionCreator(textareaOnChange))
    }
  }
}

const UsersContainer = connect(mapStateToProps, mapDispatchToProps)(MyPosts);
//debugger
export default UsersContainer;

