const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'
const SET_USER_PROFILE = 'SET-USER-PROFILE'

const initialState = {
  posts: [
    { id: 1, message: "This is my first post.", likesCount: 12 },
    { id: 1, message: "Second post. Hello everybody", likesCount: 11 },
  ],
  newPostText: "it-kamasutra",
}

const profileReducer = (state=initialState, action) => {
    //debugger
    //********* POST: ADD_POST **** ****/
    if (action.type === ADD_POST) {
      const stateCopy = {
        ...state,
        newPostText: "",
        posts: [
          ...state.posts,
          {
            id: 3,
            message: state.newPostText,
            likesCount: 0,
          },
        ],
      };
      return stateCopy;
    }
      //*********POST: UPDATE_NEW_POST_TEXT ********/
      if (action.type === UPDATE_NEW_POST_TEXT) {
        return (
          {...state, 
          newPostText: action.newText}
        )
      }

    return state
}


const addPostActionCreator = () => ({type: ADD_POST});

const updateNewPostTextActionCreator = text => (
  {
    type: UPDATE_NEW_POST_TEXT,
    newText: text
  }
);

export {addPostActionCreator, updateNewPostTextActionCreator}
export default profileReducer
