class Dialogs extends React.Component {  
  //debugger
  //debuggerconsole.log (props)
  //console.log("lllook: Dialogs");
  dialogNames = () => {
    return this.props.dialogPages.dialogs.map((dialogs, index) => (
      <DialogItem name={dialogs.name} id={dialogs.id} key={index} />
    ))
  }
  messageElements = () =>{ 
    return this.props.dialogPages.messages.map((msg, index) => (
    <Message message={msg.message} key={index}/>
  ));
 }
  onSendMessageClick = () => {
    this.props.sendMessageCreator()
  }
  messageAreaOnChange = (e) => {
    this.props.updateNewMessageBodyCreator(e.target.value)
  }

  //console.log(props.isAuth)
  //Login test, Redirecting to login Page
/*   if (!props.isAuth) {
    return <Navigate to="/login" replace={true} />
  } */
  render () {
  return (<>

  <div className="dialogs">
      <div className="dialogs-names">
        {this.dialogNames}
      </div>
      <div className="dialogs-messages"> 
        {this.messageElements}
        <div className="dialogs-text-input">
          <textarea 
          //ref={newMessage}
          onChange={this.messageAreaOnChange}
          value={props.dialogPages.newMessageBody}
          placeholder="Write your message here"></textarea>
        </div>
        <div><button onClick={onSendMessageClick}>Send</button></div>
      </div>
    </div>
  </>)
  }

};
//*** End Dialogs */

export default Dialogs;
