import * as axios from "axios";
import React from "react";
import personImg from "../../assets/images/person.png";
import './Users.css'

class Users extends React.Component {
  firstCall = true;
  constructor(props) {
    super(props);
  }

  initUsers = () => {
    const httpQuery = `https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`
    axios
      .get(httpQuery)
      .then((response) => {
        const extractData = response.data.items;
        //debugger
        this.props.setUsers(extractData);
      });
    this.props.setUsers([]);
    //debugger
  };
  showInitButton = () => {
    if (this.props.users.length === 0) {
      return <button onClick={this.initUsers}>initData</button>;
    }
    return null;
  };

  onPageChanged = pageNumber => {
    this.props.setCurrentPage(pageNumber)
    const httpQuery = `https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`
    axios
    .get(httpQuery)
    .then((response) => {
      const extractData = response.data.items;
      //debugger
      this.props.setUsers(extractData);
      this.props.setTotalUsersCount
    });
    //debugger
    /* return () */
  }

  showUsers = () => {
    return this.props.users.map((u, index) => {
      return (
        <div key={index}>
          {u.name} id={u.id}
          <span>
            <div className="user-photo">
              <img src={u.photos.small ? u.photos.small : personImg} alt="ss" />
            </div>
            <div className="">
              {!u.followed ? (
                <button onClick={() => this.props.follow(u.id)}>Follow</button>
              ) : (
                <button onClick={() => this.props.unfollow(u.id)}>
                  unFollow
                </button>
              )}
              {/* <button onClick={cruser}>createUser</button> */}
            </div>
          </span>
          <span>
            <span>
              <div className="">{u.fullname} </div>
              <div className="">{u.status} </div>
            </span>
            <span>
              <div className="">{"u.location.country"} </div>
              <div className="">{"u.location.city"} </div>
            </span>
          </span>
        </div>
      );
    });
  };
/*   pagesBlock = (pages) => {
    return pages.map((page) => {
      return <span className="u-paginator__item">{page}</span>;
    });
    //debugger
  }; */
  componentDidMount() {
    console.log("componentDidMount firstCall: " + this.firstCall);
    if (this.firstCall) {
      this.initUsers();
    }
    this.firstCall = false;
  }
  render() {
    //console.log("lllook: " + this.firstCall);
    const pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
    //const pagesCount = 5;

    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
      pages.push(i);
    }
    const classNameSrc = page => `u-paginator__item ${this.props.currentPage === page && 'u-paginator__item--active'}`
    const clickPaginator = (page) => {
      return(this.props.currentPage(page))
    }
    //debugger
    return (
      <div className="">
        <div className="">{this.showInitButton()}</div>
        <div className="u-paginator">
        {

        pages.map((page,index) => {
          return <span 
          key={index}
          className={classNameSrc(page) }
          onClick={(event) => this.onPageChanged(page)}>
              {page}
          </span>;
        })
        }
        </div>

        <div className="">{this.showUsers()}</div>
      </div>
    );
  }
}

export default Users;
