import React from "react";
import "./App.css";
import Header from "./components/Header/Header";
import Profile from "./components/Profile/Profile";
import Sidebar from "./components/Sidebar/Sidebar";
import Dialogs from "./components/Dialogs/Dialogs";

import {
  BrowserRouter,
  Switch,
  Route,
  Routes,
  Link,
  NavLink
} from "react-router-dom";

function App(props) {
  //let likes = 0
  //debugger
    return (
      <BrowserRouter>
        <div className="app-wrapper">
          <Header />
          <Sidebar />
          <main className="maincontent">
          <Routes>
            <Route path="/profile" element={<Profile 
                            profilePage = {props.store.getState().profileReducer} 
                            /* posts={props.state.profilePage.posts} */ 
                            dispatch = {props.dispatch} 

                            />} />
            <Route path="/dialogs" element={<Dialogs 
/*                             dialogs={props.state.dialogsPage.dialogs}
                            messages={props.state.dialogsPage.messages}
                            newMessageAreaTmp={props.state.dialogsPage.newMessageBody}
                            dispatch = {props.dispatch}  */
                            dispatch = {props.dispatch}
                            state={props.store.getState().dialogsReducer}
                            />} />
            <Route path="/dialogs/*" element={<Dialogs 
/*                             dialogs={props.state.dialogsPage.dialogs}
                            messages={props.state.dialogsPage.messages} */
                            />} />
            <Route path="/news" element={<News />} />
            <Route path="/hello" element={<Hello />} />
            <Route path="*" element={<NotFound />} />
          </Routes>
          </main>
        </div>
      </BrowserRouter>
    );
}

//new page
const News = () => {
  return <h2>Here's News</h2>;
}
const Hello = () => {
  return <h2>Here's Helloooo!!!! I'm in processing...</h2>;
}
const NotFound = () => {
  return <h2>Error 404! Not Found Page</h2>;
}



export default App;
