      .get(httpQuery, {
        withCredentials: true,
      }) */
      authAPI.me()
      .then((data) => {
        console.log("response.data.resultCode: " + data.resultCode);
        if (data.resultCode === 0) {
          //declare nev variables
          const { id, login, email } = data.data;
          this.props.setAuthUserData(id, login, email);
          //debugger
        }

        //debugger
