          { id: 2, name: "Dima" },
          { id: 3, name: "Roman" },
          { id: 4, name: "Greg" },
          { id: 5, name: "Nadya" },
          { id: 6, name: "Masha" },
        ],
        messages: [
          { id: 1, message: "Hi !" },
          { id: 2, message: "How are you?" },
          { id: 3, message: "Sheat happens" },
        ]
  }
  }
}

let renderEntireTree
 // = () => {
