import React from "react";
import { NavLink } from "react-router-dom"

//**** page Dialogs */
const DialogItem = (props) => {
    let path = '/dialogs/' + props.id

    return (
      <div className="dialogs__names--item">
        {/* <NavLink to={path}>{ props.name }</NavLink> */}
        <NavLink to={String(props.id)} activeclassname="active2">{ props.name } </NavLink>
      </div>
    )
  }

  const Message = (props) => {
    return (
      <div className="dialog__messages--item">{ props.message }</div>
    )
  }

  const Dialogs = (props) => {
     let dialogData = [
         {id: 1, name: 'Josef'},
         {id: 2, name: 'Dima'},
         {id: 3, name: 'Roman'},
         {id: 4, name: 'Greg'},
         {id: 5, name: 'Nadya'},
         {id: 6, name: 'Masha'}
     ] 
     let messageData = [
         {id:1, message:'Hi !'},
         {id:2, message:'How are you?'},
         {id:3, message:'Sheat happ'},
     ]
    return (
      <div className="dialogs">
        <div className="dialogs__names">
          <DialogItem name={dialogData[0].name} id={dialogData[0].id} />
          <DialogItem name={dialogData[1].name} id={dialogData[1].id} />
          <DialogItem name={dialogData[2].name} id={dialogData[2].id} />
          <DialogItem name={dialogData[3].name} id={dialogData[3].id} />
          <DialogItem name={dialogData[4].name} id={dialogData[4].id} />
          <DialogItem name={dialogData[5].name} id={dialogData[5].id} />
        </div>
        <div className="dialog__messages">
          <Message message='Hi !' />
          <Message message='How are you?' />
          <Message message='Sheat happed' />
        </div>
      </div>
    )
  }
  //*** End Dialogs */

  export default Dialogs
