import React from "react";
import './Profile.css'
import MyPostsContainer from './MyPosts/MyPostsContainer'
import Profileinfo from "./Profileinfo/Profileinfo";
import { connect } from 'react-redux'
import { setUserProfile,
         getUserProfileThunk
        } from '../../redux/reducer-profile'
import Profile from "./Profile"
import {
  Navigate, // == redirect
  useLocation,
  useNavigate,
  useParams,
  useMatch
} from "react-router-dom";
import {userAPI} from '../../api/api'
import { withAuthRedirect } from "../../hoc/withAuthRedirect";
//=== Start Class ProfileContainer ===
class ProfileContainer extends React.Component {
  componentDidMount() {
    //debugger
    let userId = 2
    // а вдруг не число?
    if ((this.props.router.params.userId) && ( parseInt(this.props.router.params.userId) > 0)) {
        userId = this.props.router.params.userId
      }


    console.log('log: DidMount ProfileContainer')

    this.props.getUserProfileThunk(userId)
  }

  render() {

    return (
      <div>
        <Profile {...this.props} 
            />
    </div>      
    );
  }
}

//=== End Class ProfileContainer ===

//rom middleware *** HOC **** Hight order component
/* const AuthRedirectComponent = (props) => {
  if (!props.isAuth) {
    return <Navigate to="/login" replace={true} />
  }
  return <ProfileContainer {...props} />
} */

const AuthRedirectComponent = withAuthRedirect(ProfileContainer)
// Возвращаем только те данные, которые нужны ребенку
const mapStateToProps = (state) => ({
  profile: state.profileReducer.profile,
  isAuth: state.auth.isAuth
})


const mapDispatchToProps = {  
  /* setUserProfile, */
  getUserProfileThunk
}

function withRouter(Component) {
  function ComponentWithRouterProp(props) {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    let match = useMatch("/profile/25714");
    return (
      <Component
        {...props}
        router={{ location, navigate, params, match }}
      />
    );
  }

  return ComponentWithRouterProp;
}

//const WithUrlDataProfileContainer = withRouter(ProfileContainer)
const WithUrlDataProfileContainer = withRouter(AuthRedirectComponent)

export default connect(mapStateToProps, mapDispatchToProps)(WithUrlDataProfileContainer);
