import React from "react";
import './Profileinfo.css'
import '../../../assets/css/socialmedia.css'
import MyPostsContainer from '../MyPosts/MyPostsContainer'
import Preloader from '../../commonBlocks/Preloader/PreloaderWaive'
import personImg from "../../../assets/images/anonyme.png";

const Profileinfo = (props) => {
  /* window.bb = props.profile.photos */
  //!props.profile && <Preloader /> //Empty data test 

  const smallPhoto = () => (
    props.profile.photos.small ? props.profile.photos.small : personImg
  )

/*   const userContacts = () => {
    const arrContacts = Object.entries(props.profile.contacts);
    window.c1 = arrContacts
    window.cc = arrContacts.map((a,b)=>(`${a}  ${b}`))
    //return  arrContacts.map((sosialSource,key)=>(<p> sosialSource: {sosialSource}  </p>))
    return  arrContacts.map((arrC,key)=>(
      arrC.map((media,key) => ((<p> media: {media} key: {key} </p>)))
      ))
  } */

  const isPropsTxt = () => (
    <div>

      <div className="x">
          <h2>avatar + description</h2> 

          <div className="user-photo__small">
            <img src={smallPhoto()} alt="small Photo" />
          </div>
          <p>About me: {props.profile.aboutMe ? props.profile.aboutMe : '...'}</p>
          <SocialMediaContacts contacts={props.profile.contacts} />       

      </div>
      {/* <MyPostsContainer /> */}
    </div>
  )

  return (
    !props.profile ? <Preloader /> : isPropsTxt()
  )
}

const SocialMediaContacts = (props) => {
  const contacts = [];

  const facebook = (link) => (link ? "fa fa-facebook" : false )
  const web = (link) => (link ? "fa fa-external-link" : null)
  const vk = (link) => (link ? "fa fa-vimeo" : null)
  const twitter = (link) => (link ? "fa fa-twitter" : null)
  const instagram = (link) => (link ? "fa fa-instagram" : null)
  const youtube = (link) => (link ? "fa fa-youtube" : null)
  const github = (link) => (link ? "fa fa-github" : null)

  facebook(props.contacts.facebook) && (contacts.push([facebook(props.contacts.facebook), props.contacts.facebook]))
  web(props.contacts.website) && (contacts.push([web(props.contacts.website), props.contacts.website]))
  vk(props.contacts.vk) && (contacts.push([vk(props.contacts.vk), props.contacts.vk]))
  twitter(props.contacts.twitter) && (contacts.push([twitter(props.contacts.twitter), props.contacts.twitter]))
  instagram(props.contacts.instagram) && (contacts.push([instagram(props.contacts.twitter), props.contacts.twitter]))
  youtube(props.contacts.youtube) && (contacts.push([youtube(props.contacts.youtube), props.contacts.youtube]))
  github(props.contacts.github) && (contacts.push([github(props.contacts.github), props.contacts.github]))

const printContacts = () => {
    if (!contacts.length) return ('User has not contacts info yet.')

    return (  contacts.map((contact, index) =>(
      <a href={contact[1]} className={contact[0]} key={index}></a>

    )))
  }

  return (
    <>
    <div className="useruser-contacts">Contacts:</div>
    {  printContacts()}
    {}
    </>
  );
}

export default Profileinfo;
