import React from "react";
import Dialogs from './Dialogs'
import { sendMessageCreator,
  updateNewMessageBodyCreator } from '../../redux/reducer-dialogs'

//**** page Dialogs */

let DialogsContainer = (props) => {
  const state = props.store.getState().dialogsReducer

  const onSendMessageClick = () => {
    props.store.dispatch(sendMessageCreator())
  }
  const messageAreaOnChange = (newMessageTmp) => {
    const action = updateNewMessageBodyCreator(newMessageTmp)
    props.store.dispatch(action)
  }

  return (
    <Dialogs 
    updateNewMessageBodyCreator={messageAreaOnChange} 
    sendMessageCreator={onSendMessageClick} 

    dialogPages={state}

/*     dialogs={state.dialogs}
    messages={state.messages}
    newMessageBody={state.newMessageBody} */
    />
  );
};

export default DialogsContainer;
