import { renderEntireTree } from '..'

const state = {
    profilePage: { 
        posts: [
            { id: 1, message: "This is my first post.", likesCount: 12 },
            { id: 1, message: "Second post. Hello everybody", likesCount: 11 },
          ],
    },
    dialogsPage: {
        dialogs: [
            { id: 1, name: "Josefina" },
            { id: 2, name: "Dima" },
            { id: 3, name: "Roman" },
            { id: 4, name: "Greg" },
            { id: 5, name: "Nadya" },
            { id: 6, name: "Masha" },
          ],
          messages: [
            { id: 1, message: "Hi !" },
            { id: 2, message: "How are you?" },
            { id: 3, message: "Sheat happens" },
          ]
    }
};

export const addPost = postMessage => {
  const newPost = {
     id: 3, 
     message: postMessage, 
     likesCount: 0
  }

  state.profilePage.posts.push(newPost)
debugger
}


export default state;
