const FOLLOW = 'FOLLOW', 
      UNFOLLOW = 'UNFOLLOW', 
      SET_USERS = 'SET_USERS',
      SET_CURRENT_PAGE = 'SET_CURRENT_PAGE';

const initialState = {
  users:[],
  pageSize: 2, //limit users
  totalUsersCount: 8,
  currentPage: 3
};

const usersReducer = (state=initialState, action) => {
    if (action.type === FOLLOW) {

        const stateCopy = { 
            ...state, 
            users: state.users.map ( //возвр новый объект
                user => { 
                  return (user.id === action.userID) ? {...user,followed: true} : user;
                }
            )
        }
        //debugger
        return stateCopy
    }
    if (action.type === UNFOLLOW) {

        const stateCopy = { 
            ...state, 
            users: state.users.map ( //возвр новый объект
                user => { 
                    return (user.id === action.userID) ? {...user,followed: false} : user;
                }
            )
        }
        //debugger
        return stateCopy
    }
    if (action.type === SET_USERS) {
        const stateCopy = { 
            //...state, users: [...state.users, ...action.users]
        }
        //debugger
        return stateCopy
    }
    if (action.type === SET_CURRENT_PAGE) {
        //debugger
        const stateCopy = { 
            ...state, currentPage: action.currentPage
        }
        //debugger
        return stateCopy
    }    
    return state
}

const followAC = (userID) => ({ type: FOLLOW, userID}); //create action.type, action.userID
const unfollowAC = (userID) => ({ type: UNFOLLOW, userID});//create action.type, action.userID
const setUsersAC = users => ({ type: SET_USERS, users});//create action.type, action.users
const setCurrentPageAC = currentPage => ({ type: SET_CURRENT_PAGE, currentPage});

export {followAC, unfollowAC, setUsersAC, setCurrentPageAC }
export default usersReducer
