import React from "react";
import Dialogs from "./Dialogs";
import {
  sendMessageCreator,
  updateNewMessageBodyCreator,
} from "../../redux/reducer-dialogs";
import StoreContext from "../../StoreContext";

//**** page Dialogs */

let DialogsContainer = () => {
  return (
    <StoreContext.Consumer>
      {(store) => {
        const state = store.getState().dialogsReducer;

        const onSendMessageClick = () => {
          store.dispatch(sendMessageCreator());
        };
        const messageAreaOnChange = (newMessageTmp) => {
          const action = updateNewMessageBodyCreator(newMessageTmp);
          store.dispatch(action);
        };

        return (
          <Dialogs
            updateNewMessageBodyCreator={messageAreaOnChange}
            sendMessageCreator={onSendMessageClick}
            dialogPages={state}
          />
        );
      }}
    </StoreContext.Consumer>
  );
};

const SuperDialogsContainer = connect()

export default DialogsContainer;
