  componentDidMount() {
    console.log('log: DidMount ProfileContainer')
    const httpQuery = `https://social-network.samuraijs.com/api/1.0/profile/2`;
    axios.get(httpQuery).then((response) => {
      const extractData = response.data;
      //debugger
      this.props.setUserProfile(response.data);
  })
  }

  render() {
    return (
      <div>
