import React from "react";
import ReactDOM from "react-dom";

class Test3 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      courses: [],
      course: "",
      coursesSelect: ['all', 'react', 'java']
    };

  }



  handleChange = event => {
    this.setState({ value: event.target.value });
  };

  handleChangeCourse = event => {
    this.setState({ course: event.target.value });
  };

  getUnique(arr, comp) {
    const unique = arr
      //store the comparison values in array
      .map(e => e[comp])

      // store the keys of the unique objects
      .map((e, i, final) => final.indexOf(e) === i && i)

      // eliminate the dead keys & store unique objects
      .filter(e => arr[e])

      .map(e => arr[e]);
    console.log(unique);
    return unique;
  }

  componentDidMount() {
    const courses = require("./courses.json");
    this.setState({ courses: courses });
  }

  render() {

    const uniqueCouse = this.getUnique(this.state.courses, "tag");

    const courses = this.state.courses;
    const course = this.state.course;

    const filterDropdown = courses.filter(function(result) {
      return result.tag === course;
    });

    return (
      <div>


<form>
          <br />
          <br />
          <label>
            Looping through Courses tag from Json File
            <select
              value={this.state.course}
              onChange={this.handleChangeCourse}
            >
              {uniqueCouse.map(course => (
                <option key={course.id} value={course.tag}>
                  {course.tag}
                </option>
              ))}
            </select>
          </label>

          <div>
            {filterDropdown.map(course => (
              <div key={course.id} style={{ margin: "10px" }}>
                {course.course}
                <br />
              </div>
            ))}
          </div>
        </form>


        <hr>

        </hr>
        <div><pre>{JSON.stringify(this.state, null, 2) }</pre></div>;
      </div>
    );
  }
}

export default Test3
