import React from "react";
import MyPosts from './MyPosts'
import { addPostActionCreator, updateNewPostTextActionCreator } from '../../../redux/reducer-profile'


const MyPostsContainer = (props) => {

  const state = props.store.getState().profileReducer

  const onAddPost = () => {
    props.store.dispatch(addPostActionCreator())
  };
    //debugger
  const textareaOnChange = (text) => {
    const action = updateNewPostTextActionCreator(text)
    props.store.dispatch(action)
  }


  return (<MyPosts 
    updateNewPostText={textareaOnChange} 
    addPost={onAddPost} 
    posts={state.profileP.posts} />)
};

export default MyPostsContainer;
