const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY'
const SEND_MESSAGE = 'SEND-MESSAGE'

const initialState = {
  dialogs: [
    { id: 1, name: "Josefina" },
    { id: 2, name: "Dima" },
    { id: 3, name: "Roman" },
    { id: 4, name: "Greg" },
    { id: 5, name: "Nadya" },
    { id: 6, name: "Masha" },
  ],
  messages: [
    { id: 1, message: "Hi !" },
    { id: 2, message: "How are you?" },
    { id: 3, message: "Sheat happens" },
  ],
  newMessageBody: ''
}

const dialogsReducer = (state=initialState, action) => {
  //debugger
    //*********MESSAGE: UPDATE_NEW_MESSAGE_BODY ********/
    if (action.type === UPDATE_NEW_MESSAGE_BODY) {
        const stateCopy = {
          ...state,
          newMessageBody: action.messageBody
        }
        return stateCopy
      }
      //*********MESSAGE: SEND_MESSAGE ********/
      if (action.type === SEND_MESSAGE) {
        const stateCopy = {
          ...state,
          newMessageBody: '',
          messages: [
            ...state.messages, 
            { id: 4, message: state.newMessageBody }
          ]
        }
        return stateCopy
      }

    return state
}

export const sendMessageCreator = (newMessage) => ({type: SEND_MESSAGE});

export const updateNewMessageBodyCreator = msgBody => (
  {
    type: UPDATE_NEW_MESSAGE_BODY,
    messageBody: msgBody
  }
);

export default dialogsReducer
