import React from "react";
import  { Navigate } from 'react-router-dom'
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import { sendMessageCreator,
  updateNewMessageBodyCreator } from '../../redux/reducer-dialogs'

//**** page Dialogs */

let Dialogs = (props) => {
  //debugger
  //console.log("lllook: Dialogs");
  const dialogNames = props.dialogPages.dialogs.map((dialogs, index) => (
    <DialogItem name={dialogs.name} id={dialogs.id} key={index} />
  ));
  const messageElements = props.dialogPages.messages.map((msg, index) => (
    <Message message={msg.message} key={index}/>
  ));
  const onSendMessageClick = () => {
    props.sendMessageCreator()
  }
  const messageAreaOnChange = (e) => {
    props.updateNewMessageBodyCreator(e.target.value)
  }

  //console.log(props.isAuth)
  /* if (!props.isAuth) return <Navigate to="/login" replace={true} /> */
  !props.isAuth ? <Navigate to='/login' replace={true} /> : ''

  return (
    <div className="dialogs">
      <div className="dialogs-names">
        {dialogNames}
      </div>
      <div className="dialogs-messages"> 
        {messageElements}
        <div className="dialogs-text-input">
          <textarea 
          //ref={newMessage}
          onChange={messageAreaOnChange}
          value={props.dialogPages.newMessageBody}
          placeholder="Write your message here"></textarea>
        </div>
        <div><button onClick={onSendMessageClick}>Send</button></div>
      </div>
    </div>
  );
};
//*** End Dialogs */

export default Dialogs;
