import * as axios from "axios";

const instance = axios.create (
    {
        base
    }
);

export const getUsers = (currentPage = 1, pageSize = 10) => {
    const httpQuery = `https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pageSize}`;

    return axios.get(httpQuery,{withCredentials: true})
        .then(response => response.data)
}
