import * as axios from "axios";

const instance = axios.create (
    {
        baseURL: 'https://social-network.samuraijs.com/api/1.0/',
        withCredentials: true,
        headers: {'API-KEY': '46f80780-8cc7-4980-bf49-abc33bba8e09'}
    }
);

export const userAPI = {
    getUsers (currentPage = 1, pageSize = 10) {
        const httpQuery = `users?page=${currentPage}&count=${pageSize}`;
        return instance.get(httpQuery)
            .then(response => response.data)
    },  
    follow(userId) { 
        const httpQuery = `https://social-network.samuraijs.com/api/1.0/follow/${userId}`;
        return instance.post(httpQuery)
        .then(response => response.data)
    },
    unfollow(userId) { 
        const httpQuery = `https://social-network.samuraijs.com/api/1.0/follow/${userId}`;
        return instance.delete(httpQuery)
        .then(response => response.data)
    }

}
