import React from "react";

class ProfileStatus extends React.Component {
    state = {
        editMode: false
    }

    render() {
        const Ticle =() =>(
            <div className='profile-status__ticle'>
            {this.props.status}
        </div>
        )

        const InputArea =() =>(
            <div className='profile-status__input'>
                <input value={this.props.status} />
            </div>
        )

        return (
            <>  
                <Ticle />
            </>
        )
    }
}

export default ProfileStatus
