import React from "react";
import { follow, 
         unfollow, 
         setUsers, 
         setCurrentPage,
         setTotalUsersCount,
         setIsFetching } from '../../redux/reducer-users'
//{followAC, unfollowAC, setUsersAC} //reducer-users
import { connect } from 'react-redux'
import * as axios from "axios";
import Users from './Users';
import Preloader1 from '../../components/commonBlocks/Preloader/Preloader1'
import Preloader from '../../components/commonBlocks/Preloader/Preloader'
//***** Start insert UsersAPIComponent */



class UsersContainer extends React.Component {
  firstCall = true;
  constructor(props) {
    super(props);
  }

  initUsers = () => {
    this.props.setIsFetching (true)
    const httpQuery = `https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`;
    axios.get(httpQuery).then((response) => {
      const extractData = response.data.items;
      //debugger
      this.props.setIsFetching (false)
      this.props.setUsers(extractData);
      this.props.setTotalUsersCount(response.data.totalCount);
    });
    this.props.setUsers([]);
    //debugger

  };
  showInitButton = () => {
    if (this.props.users.length === 0) {

      return <button onClick={this.initUsers}>initData</button>;
    }
    return null;
  };

  onPageChanged = (pageNumber) => {
    //debugger
    this.props.setIsFetching (true)
    this.props.setCurrentPage(pageNumber);
    const httpQuery = `https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`;
    axios.get(httpQuery).then((response) => {
      const extractData = response.data.items;
      this.props.setIsFetching (false)
      this.props.setUsers(extractData);
    });
  };

  componentDidMount() {


    console.log("componentDidMount firstCall: " + this.firstCall);
    if (this.firstCall) {
      this.initUsers();
    }
    this.firstCall = false;

    /* this.props.setIsFetchingAC (false) */
  }
  render() {
    return (
      <>
        { this.props.isFetching && <Preloader /> }
        {/* <div className="">{this.showInitButton()}</div> */}

        <Users
          users={this.props.users}
          follow={this.props.follow}
          unfollow={this.props.unfollow}
          onPageChanged={this.onPageChanged}
          currentPage={this.props.currentPage}
          pageSize={this.props.pageSize}
          totalUsersCount = {this.props.totalUsersCount}
          /* isFetching={this.props.isFetching} */
        />

      </>
    );
  }
}
//***** End insert UsersAPIComponent */

// Возвращаем только те данные, которые нужны ребенку
const mapStateToProps = (state) => {
  //console.log('hello!')
  //debugger
  return {
    users: state.usersPage.users, //usersPage: usersReducer //property is diferent because
    pageSize: state.usersPage.pageSize, //limit users
    totalUsersCount: state.usersPage.totalUsersCount,
    currentPage: state.usersPage.currentPage,
    isFetching: state.usersPage.isFetching
  }
}
