const AuthRedirectComponent = withAuthRedirect(ProfileContainer)
// Возвращаем только те данные, которые нужны ребенку
const mapStateToProps = (state) => ({
  profile: state.profileReducer.profile,
  isAuth: state.auth.isAuth
}
)
const mapDispatchToProps = {  
  /* setUserProfile, */
  getUserProfileThunk

}

function withRouter(Component) {
  function ComponentWithRouterProp(props) {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    let match = useMatch("/profile/25714");
    return (
      <Component
        {...props}
        router={{ location, navigate, params, match }}
      />
    );
  }

  return ComponentWithRouterProp;
}

//const WithUrlDataProfileContainer = withRouter(ProfileContainer)
const WithUrlDataProfileContainer = withRouter(AuthRedirectComponent)

export default connect(mapStateToProps, mapDispatchToProps)(WithUrlDataProfileContainer);
