const FOLLOW = 'FOLLOW', UNFOLLOW = 'UNFOLLOW', SET_USERS = 'SET_USERS';

const initialState = {
  users: [
    {
      id: 1,
      photoUrl: 'https://randomuser.me/api/portraits/med/men/5.jpg',
      followed: false,
      fullname: "Dmitry",
      status: "hello it's my status",
      location: { city: "Minsk", country: "Belarus" },
    },
    {
      id: 2,
      photoUrl: 'https://randomuser.me/api/portraits/med/men/2.jpg',
      followed: true,
      fullname: "Roman",
      status: "Roman everywhere",
      location: { city: "Omsk", country: "Russia" },
    },
    {
      id: 3,
      photoUrl: 'https://randomuser.me/api/portraits/med/men/3.jpg',
      followed: false,
      fullname: "Ilon Musk",
      status: "Follow me to the Mars",
      location: { city: "NY", country: "USA" },
    },
  ]
};

const usersReducer = (state=initialState, action) => {
    if (action.type === FOLLOW) {

        const stateCopy = { 
            ...state, 
            users: state.users.map ( //возвр новый объект
                user => { 
                  return (user.id === action.userID) ? {...user,followed: true} : user;
                }
            )
        }
        //debugger
        return stateCopy
    }
    if (action.type === UNFOLLOW) {

        const stateCopy = { 
            ...state, 
            users: state.users.map ( //возвр новый объект
                user => { 
                    return (user.id === action.userID) ? {...user,followed: false} : user;
                }
            )
        }
        //debugger
        return stateCopy
    }
    if (action.type === SET_USERS) {
        const stateCopy = { 
            ...state, users: [...state.users, ...action.users]
        }
        return stateCopy
    }

    return state
}

const followAC = (userID) => ({ type: FOLLOW, userID}); //create a followaction
const unfollowAC = (userID) => ({ type: UNFOLLOW, userID});
const setUsersAC = users => ({ type: SET_USERS, users});

export {followAC, unfollowAC, setUsersAC}
export default usersReducer
