const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY'
const SEND_MESSAGE = 'SEND-MESSAGE'

const initialState = {
  dialogs: [
    { id: 1, name: "Josefina" },
    { id: 2, name: "Dima" },
    { id: 3, name: "Roman" },
    { id: 4, name: "Greg" },
    { id: 5, name: "Nadya" },
    { id: 6, name: "Masha" },
  ],
  messages: [
    { id: 1, message: "Hi !" },
    { id: 2, message: "How are you?" },
    { id: 3, message: "Sheat happens" },
  ],
  newMessageBody: ''
}

const dialogsReducer = (state=initialState, action) => {
  //debugger
    //*********MESSAGE: UPDATE_NEW_MESSAGE_BODY ********/
    if (action.type === UPDATE_NEW_MESSAGE_BODY) {
        const stateCopy = {
          ...state,
          newMessageBody: action.messageBody
        }
        return stateCopy
        //state.newMessageBody = action.messageBody;
        //return {...state} //object has changed!
      }
      //*********MESSAGE: SEND_MESSAGE ********/
      if (action.type === SEND_MESSAGE) {


        const messageBody = state.newMessageBody
        state.messages.push({ id: 5, message: messageBody })
        state.newMessageBody = '';

        return {...state}
      }

    return state
}

const sendMessageCreator = () => ({type: SEND_MESSAGE});

const updateNewMessageBodyCreator = msgBody => (
  {
    type: UPDATE_NEW_MESSAGE_BODY,
    messageBody: msgBody
  }
);

export {sendMessageCreator, updateNewMessageBodyCreator}
export default dialogsReducer
