import React from "react";
import { NavLink } from "react-router-dom";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import { sendMessageCreator,
  updateNewMessageBodyCreator } from '../../redux/reducer-dialogs'

//**** page Dialogs */

let Dialogs = (props) => {


  //const state = props.store.getState().dialogsPage
  const state = props.dialogs //.store.getState().dialogsReducer
  debugger
  const dialogNames = props.dialogs.dialogs.map((dialogs, index) => (
    <DialogItem name={dialogs.name} id={dialogs.id} key={index} />
  ));
  const messageElements = props.dialogs.messages.map((msg, index) => (
    <Message message={msg.message} key={index}/>
  ));
  const onSendMessageClick = () => {
    //console.log(newMessage.current.value)
    props.dispatch(sendMessageCreator())
  }
  const messageAreaOnChange = (e) => {
    //console.log(e.target.value)
    //props.dispatch(updateNewMessageBodyCreator(newMessage.current.value))
    props.dispatch(updateNewMessageBodyCreator(e.target.value))
  }
  //const newMessage = React.createRef();

  return (
    <div className="dialogs">
      <div className="dialogs-names">
        {dialogNames}
      </div>
      <div className="dialogs-messages"> 
        {messageElements}
        <div className="dialogs-text-input">
          <textarea 
          //ref={newMessage}
          onChange={messageAreaOnChange}
          value={props.newMessageBody}
          placeholder="Write your message here"></textarea>
        </div>
        <div><button onClick={onSendMessageClick}>Send</button></div>
      </div>
    </div>
  );
};
//*** End Dialogs */

export default Dialogs;
