//import { renderEntireTree } from '../index'
const store = {
  _state: {
    profilePage: {
      posts: [
        { id: 1, message: "This is my first post.", likesCount: 12 },
        { id: 1, message: "Second post. Hello everybody", likesCount: 11 },
      ],
      newPostText: "it-kamasutra",
    },
    dialogsPage: {
      dialogs: [
        { id: 1, name: "Josefina" },
        { id: 2, name: "Dima" },
        { id: 3, name: "Roman" },
        { id: 4, name: "Greg" },
        { id: 5, name: "Nadya" },
        { id: 6, name: "Masha" },
      ],
      messages: [
        { id: 1, message: "Hi !" },
        { id: 2, message: "How are you?" },
        { id: 3, message: "Sheat happens" },
      ],
    },
  },
  renderEntireTree() {
    console.log('State has changed')
  },
  addPost () {
    const newPost = {
      id: 3,
      message: state.profilePage.newPostText,
      likesCount: 0,
    };

    state.profilePage.posts.push(newPost);
    state.profilePage.newPostText = "";
    renderEntireTree(state);
  }
};


const addPost = () => {
  const newPost = {
    id: 3,
    message: state.profilePage.newPostText,
    likesCount: 0,
  };

  state.profilePage.posts.push(newPost);
  state.profilePage.newPostText = "";
  renderEntireTree(state);
};

const updateNewPostText = (newText) => {
  state.profilePage.newPostText = newText;
  renderEntireTree(state);
};

const subscribe = (observer) => {
  renderEntireTree = observer;
};

window.state = state

export { addPost, updateNewPostText, subscribe };

export default state;
