import React from "react";
import s from "./MyPosts.module.css";
import Post from "./Post/Post";
import { addPostActionCreator, updateNewPostTextActionCreator } from '../../../redux/state'


const MyPosts = (props) => {

  const newPostElement = React.createRef();

  //**** start event analyse ****
  const btnAddPost = () => {
    //props.dispatch({ type: 'ADD-POST' })
    props.dispatch({ type: 'ADD-POST' })
    //addPostActionCreator()
    /* props.addPost(); */
    //props.updateNewPostText('') //CLEAR INPUT
  };
    //debugger
  const textareaOnChange = () => {
    //props.dispatch({ type: 'UPDATE-NEW-POST-TEXT', newText: newPostElement.current.value })
    updateNewPostTextActionCreator(newPostElement.current.value)

    /* props.updateNewPostText(newPostElement.current.value) */
  /*   const text = newPostElement.current.value;
    console.log(text) */
  }

  //**** End event analyse ****

  return (
    <div className={s.item}>
      <h2>Hi MyPosts!</h2>
      <div className="x">
        <textarea 
          ref={newPostElement} 
          value={props.newPostText}
          onChange={textareaOnChange}
          >
        </textarea>
      </div>
      <div className="x">
        <button onClick={btnAddPost}>Add post</button>
      </div>
      <hr />
      {props.posts.map((post, index) => (
        <Post message={post.message} likesCount={post.likesCount} key={index} />
      ))}
    </div>
  );
};

export default MyPosts;
