import React from "react";
import Users from './Users'
import { followAC, unfollowAC, setUsersAC } from '../../redux/reducer-users'
//{followAC, unfollowAC, setUsersAC} //reducer-users
import { connect } from 'react-redux'

// Возвращаем только те данные, которые нужны пользователям.
const mapStateToProps = (state) => {
  //console.log('hello!')
  return {
    users: state.usersPage.users
  }
}
//Создаем dispatch
const mapDispatchToProps = (dispatch) => {  
  return {
    follow: userID => {dispatch(followAC(userID))},
    unfollow: userID => {dispatch(unfollowAC(userID))},
    setUser: userID => {}
  }
}

const UsersContainer = connect(mapStateToProps, mapDispatchToProps)(MyPosts);
//debugger
export default UsersContainer;

