import React from "react";

const Users = (props) => {
    const cruser = () => {
        let s
        if (props.users.length) {s='hello '+props.users.length};
        if (!props.users.length) {
            s='Look '+props.users.length
            props.setUsers(
                [
                    {
                        id: 1,
                        photoUrl: 'https://randomuser.me/api/portraits/med/men/5.jpg',
                        followed: false,
                        fullname: "Dmitry",
                        status: "hello it's my status",
                        location: { city: "Minsk", country: "Belarus" },
                      }
                ]
            )
        };
    } 

    debugger
    return (

        props.users.map((u) =>{
           return(
               <div className="">
                   <button onClick={cruser}>createUser</button>
            <div> key={u.id} {u.fullname} 
           <span>
               <div className=""><img src={u.photoUrl} alt="ss" /></div>
               <div className="">
               {
               !u.followed ? 
               <button onClick={()=>props.follow(u.id)}>Follow</button>
               : <button onClick={()=>props.unfollow(u.id)}>unFollow</button>
               }

                   </div>

           </span>
           <span>
               <span>
                   <div className="">{u.fullname} </div>
                   <div className="">{u.status} </div>
               </span>
               <span>
                    <div className="">{u.location.country} </div>    
                    <div className="">{u.location.city} </div>
               </span>
            </span>
           </div>
           </div>
        )
        })
    )
}

export default Users
