import React from "react";
import { NavLink } from "react-router-dom";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";

//**** page Dialogs */

const Dialogs = (props) => {
  const messageElements = props.messages.map((msg, index) => (
    <Message message={msg.message} key={index}/>
  ));


  return (
    <div className="dialogs">
      <div className="dialogs-names">
        {props.dialogs.map((dialogs, index) => (
          <DialogItem name={dialogs.name} id={dialogs.id} key={index} />
        ))}
      </div>
      <div className="dialogs-messages"> {messageElements}
        <div className="dialogs-text-input">
          <textarea placeholder="Write your message here"></textarea>
        </div>
        <div><button onClick={() => {alert('salut!')}}>Send</button></div>
      </div>
    </div>
  );
};
//*** End Dialogs */

export default Dialogs;
