import React from "react";
import Dialogs from "./Dialogs";
import {
  sendMessageCreator,
  updateNewMessageBodyCreator,
} from "../../redux/reducer-dialogs";
import { connect } from 'react-redux'
import { withAuthRedirect } from "../../hoc/withAuthRedirect";


class DialogsContainer extends React.Component {
  componentDidMount() {
    console.log ('DialogsContainer props: ' + this.props.dialogPages.dialogs[0].name)
  }
  render() {

    //debugger
    return (
      <div>
        <Dialogs {...this.props} 
            />
    </div>      
    );
  }
}



let mapStateToProps = (state) => {
  //console.log(state.auth.isAuth)
  return {
    dialogPages: state.dialogsReducer,
    isAuth: state.auth.isAuth,
  };
};

let mapDispatchToProps = (dispatch) => {
  return {
    sendMessageCreator: () => {
      dispatch(sendMessageCreator());
    },
    updateNewMessageBodyCreator: (newMessageTmp) => {
      dispatch(updateNewMessageBodyCreator(newMessageTmp));
    },
  };
};
const yy = withAuthRedirect(DialogsContainer)
const xx = connect(mapStateToProps, mapDispatchToProps)(yy);


