import React from "react";
import { NavLink } from "react-router-dom"

//**** page Dialogs */
const DialogItem = (props) => {
    let path = '/dialogs/' + props.id

    return (
      <div className="dialogs__names--item">
        {/* <NavLink to={path}>{ props.name }</NavLink> */}
        <NavLink to={String(props.id)} activeclassname="active2">{ props.name } </NavLink>
      </div>
    )
  }

  const Message = (props) => {
    return (
      <div className="dialog__messages--item">{ props.message }</div>
    )
  }

  const Dialogs = (props) => {
     let di = [
         {id: 1, name: 'Josef'},
         {id: 2, name: 'Dima'},
         {id: 3, name: 'Roman'},
         {id: 4, name: 'Greg'},
         {id: 5, name: 'Nadya'},
         {id: 6, name: 'Masha'}
     ] 
     let messages = [
         {id:1, message:'Hi !'},
         {id:2, message:'How are you?'},
         {id:3, message:'Sheat happens'}
     ]
    return (
      <div className="dialogs">
        <div className="dialogs__names">
          {di.map(dialogs => 
            (<DialogItem name={dialogs.name} id={dialogs.id} />))}
        </div>
        <div className="dialog__messages">
            {messages.map(msg => 
                (<Message message={msg.message} />))}
        </div>
      </div>
    )
  }
  //*** End Dialogs */

  export default Dialogs
