//import { renderEntireTree } from '../index'
const ADD_POST = 'ADD-POST'

const store = {
  _state: {
    profilePage: {
      posts: [
        { id: 1, message: "This is my first post.", likesCount: 12 },
        { id: 1, message: "Second post. Hello everybody", likesCount: 11 },
      ],
      newPostText: "it-kamasutra",
    },
    dialogsPage: {
      dialogs: [
        { id: 1, name: "Josefina" },
        { id: 2, name: "Dima" },
        { id: 3, name: "Roman" },
        { id: 4, name: "Greg" },
        { id: 5, name: "Nadya" },
        { id: 6, name: "Masha" },
      ],
      messages: [
        { id: 1, message: "Hi !" },
        { id: 2, message: "How are you?" },
        { id: 3, message: "Sheat happens" },
      ],
    },
  },
  _callSubscriber() {
    console.log("State has changed");
  },
  subscribe(observer) {
    this._callSubscriber = observer;
  },
  getState() {
    return this._state;
  },

  dispatch(action) { //{ type: 'ADD-POST' }
    //debugger
    if (action.type === 'ADD-POST') {
      const newPost = {
        id: 3,
        message: this._state.profilePage.newPostText,
        likesCount: 0,
      };

      this._state.profilePage.posts.push(newPost);
      this._state.profilePage.newPostText = "";
      this._callSubscriber(this._state);
      return 1;
    }
    if (action.type === 'UPDATE-NEW-POST-TEXT') {
      this._state.profilePage.newPostText = action.newText;
      this._callSubscriber(this._state);
      return 1;
    }

    return 0;
  },
};


window.store = store

//export { addPost, updateNewPostText, subscribe };
const addPostActionCreator = () => {
  return {
    type: 'ADD-POST'
  }
}

const updateNewPostTextActionCreator = text => {
  return {
    type: 'UPDATE-NEW-POST-TEXT',
    newText: text
  }
}

export { addPostActionCreator, updateNewPostTextActionCreator };
export default store;
