import React from "react";

class ProfileStatus extends React.Component {
    state = {
        editMode: false
    }

    activateEditMode = () => {
        this.state.editMode = true
        console.log("Hey")
    }

    render() {
        const Ticker =() =>(
            <div className='profile-status__ticle' 
            onDoubleClick={ this.activateEditMode } >
            {this.props.status}
        </div>
        )

        const InputArea =() =>(
            <div className='profile-status__input'>
                <input defaultValue={this.props.status} />
            </div>
        )

        return (
            <>  
                {this.editMode ? <InputArea /> : <Ticker />}

            </>
        )
    }
}

export default ProfileStatus
