import React from "react";
import './Profile.css'
import MyPostsContainer from './MyPosts/MyPostsContainer'
import Profileinfo from "./Profileinfo/Profileinfo";
import * as axios from "axios";
import { connect } from 'react-redux'
import { setUserProfile } from '../../redux/reducer-profile'
import Profile from "./Profile"
import {
  useLocation,
  useNavigate,
  useParams,
  useRouteMatch
} from "react-router-dom";

class ProfileContainer extends React.Component {
/*   constructor(props) {
    super(props);
  } */

  componentDidMount() {
    //debugger
    console.log('log: DidMount ProfileContainer')
    const httpQuery = `https://social-network.samuraijs.com/api/1.0/profile/2`;
    axios.get(httpQuery).then((response) => {
      //debugger
      this.props.setUserProfile(response.data);
      console.log('log response.data : ' + response.data.aboutMe)
  })
  }

  render() {
    //debugger
    return (
      <div>
        <Profile {...this.props} 
            profile={this.props.profile}/>
        {/* <MyPostsContainer /> */}
    </div>      
    );
  }
}

// Возвращаем только те данные, которые нужны ребенку
const mapStateToProps = (state) => ({
  profile: state.profileReducer.profile
}
)
const mapDispatchToProps = {  
  setUserProfile

}

function withRouter(Component) {
  function ComponentWithRouterProp(props) {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    let match = useRouteMatch("/blog/:slug");
    return (
      <Component
        {...props}
        router={{ location, navigate, params,  }}
      />
    );
  }

  return ComponentWithRouterProp;
}

ProfileContainer = withRouter(ProfileContainer)

export default connect(mapStateToProps, mapDispatchToProps)(ProfileContainer);
